import React, { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import axios from "axios";

import {
  Package,
  ShoppingBag,
  Tag,
  BarChart2,
  ChevronRight,
} from "lucide-react";

import AdminHeader from "./AdminHeader";

export default function HomeAdmin() {

  const navigate = useNavigate();
  const adminToken = localStorage.getItem("adminToken");

  // ========= STATE =========
  const [products, setProducts] = useState([]);
  const [discounts, setDiscounts] = useState([]);

  // ========= LOAD API =========
  useEffect(() => {

    if (!adminToken) {
      navigate("/admin/login");
      return;
    }

    const config = {
      headers: {
        Authorization: "Bearer " + adminToken
      }
    };

    // Load sản phẩm
    axios
      .get("http://localhost:8080/api/products", config)
      .then((res) => {
        const data = Array.isArray(res.data) ? res.data : res.data.data;
        setProducts(data || []);
      })
      .catch((err) => {
        console.error("Lỗi load sản phẩm:", err);
        if (err.response?.status === 401) {
          localStorage.removeItem("adminToken");
          navigate("/admin/login");
        }
      });
  }, []);


  return (
    <>
      <AdminHeader />
      <div style={{ height: 80 }}></div>

      <div style={styles.wrapper}>
        <div style={styles.container}>

          {/* QUẢN LÝ SẢN PHẨM  */}
          <div style={styles.card}>
            <div style={styles.cardHeader}>
              <h2 style={styles.cardTitle}>Quản Lý Sản Phẩm</h2>
              <Link to="/admin/products" style={styles.more}>Xem thêm</Link>
            </div>

            <div style={styles.row4}>

              <Link to="/admin/products" style={styles.itemBox}>
                <Package size={26} color="#ee4d2d" />
                <p>Số sản phẩm</p>
                <span>{products.length}</span>
              </Link>

              <Link to="/admin/products/add" style={styles.itemBox}>
                <Tag size={26} color="#3b82f6" />
                <p>Thêm sản phẩm</p>
                <span>Tạo mới</span>
              </Link>
              <Link to="/admin/products/add" style={styles.itemBox}>
                <Tag size={26} color="#3b82f6" />
                <p>dn</p>
                <span>Tạo mới</span>
              </Link>

            </div>
          </div>


          {/* --- KHỐI ĐƠN HÀNG --- */}
          <div style={styles.card}>
            <div style={styles.cardHeader}>
              <h2 style={styles.cardTitle}>Quản Lý Đơn Hàng</h2>
              <Link to="/admin/orders" style={styles.more}>Xem thêm</Link>
            </div>

            <div style={styles.row4}>
              <Link to="/admin/orders?status=processing" style={styles.itemBox}>
                <ShoppingBag size={26} color="#ef4444" />
                <p>Chờ xử lý</p>
                <span>5</span>
              </Link>

              <Link to="/admin/orders?status=shipping" style={styles.itemBox}>
                <ShoppingBag size={26} color="#3b82f6" />
                <p>Đang giao</p>
                <span>12</span>
              </Link>

              <Link to="/admin/orders?status=completed" style={styles.itemBox}>
                <ShoppingBag size={26} color="#22c55e" />
                <p>Hoàn thành</p>
                <span>31</span>
              </Link>

              <Link to="/admin/orders?status=cancelled" style={styles.itemBox}>
                <ShoppingBag size={26} color="#6b7280" />
                <p>Đã hủy</p>
                <span>3</span>
              </Link>
            </div>
          </div>


          {/* --- KHỐI DOANH THU --- */}
          <div style={styles.card}>
            <div style={styles.cardHeader}>
              <h2 style={styles.cardTitle}>Thống Kê Doanh Thu</h2>
              <Link to="/admin/new/new" style={styles.more}>Xem thêm</Link>
            </div>

            <div style={styles.row3}>
              <div style={styles.statBox}>
                <BarChart2 size={28} color="#ef4b2d" />
                <p>Doanh thu hôm nay</p>
                <h3>₫1,250,000</h3>
              </div>

              <div style={styles.statBox}>
                <BarChart2 size={28} color="#16a34a" />
                <p>Doanh thu tháng</p>
                <h3>₫32,550,000</h3>
              </div>

              <div style={styles.statBox}>
                <BarChart2 size={28} color="#3b82f6" />
                <p>Tổng lượt mua</p>
                <h3>580</h3>
              </div>
            </div>
          </div>

          {/* --- KHỐI SỰ KIỆN SHOP --- */}
          <div style={styles.card}>
            <div style={styles.cardHeader}>
              <h2 style={styles.cardTitle}>Chương Trình Của Shop</h2>
              <Link to="/admin/events" style={styles.more}>Xem thêm</Link>
            </div>

            <div style={styles.eventBox}>
              <div>
                <h3 style={styles.eventTitle}>Flash Sale cuối tuần</h3>
                <p style={styles.eventDesc}>Giảm giá mạnh cho toàn bộ sản phẩm.</p>
              </div>
              <ChevronRight size={22} color="#888" />
            </div>

            <div style={styles.eventBox}>
              <div>
                <h3 style={styles.eventTitle}>Ưu đãi khách hàng mới</h3>
                <p style={styles.eventDesc}>Voucher giảm 10% cho đơn đầu tiên.</p>
              </div>
              <ChevronRight size={22} color="#888" />
            </div>
          </div>

        </div>
      </div>
    </>
  );
}


