import React, { useEffect, useState } from "react";
import axios from "axios";
import { useParams, useNavigate } from "react-router-dom";
import AdminHeader from "../page/AdminHeader";

export default function EditProduct() {
  const { id } = useParams();
  const navigate = useNavigate();
  const token = localStorage.getItem("adminToken");

  const [form, setForm] = useState({});
  const [oldImages, setOldImages] = useState([]);
  const [deleteImagesId, setDeleteImagesId] = useState([]);
  const [newImages, setNewImages] = useState([]);

  useEffect(() => {
    if (!token) {
      navigate("/admin/login");
      return;
    }

    axios
      .get(`http://localhost:8080/api/admin/products/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      })
      .then((res) => {
        setForm(res.data.data);
        setOldImages(res.data.data.images || []);
      })
      .catch((err) => {
        console.log(err);
        if (err.response?.status === 401) {
          localStorage.removeItem("adminToken");
          navigate("/admin/login");
        }
      });
  }, [id]);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const deleteOldImage = (imgId) => {
    setDeleteImagesId([...deleteImagesId, imgId]);
    setOldImages(oldImages.filter((img) => img.id !== imgId));
  };

  const handleAddImages = (e) => {
    const files = Array.from(e.target.files);
    setNewImages([...newImages, ...files]);
  };

  const deleteNewImage = (index) => {
    const arr = [...newImages];
    arr.splice(index, 1);
    setNewImages(arr);
  };

  const handleSubmit = async () => {
    const data = new FormData();

    Object.keys(form).forEach((key) => data.append(key, form[key]));
    deleteImagesId.forEach((img) => data.append("deleteImagesId", img));
    newImages.forEach((img) => data.append("images", img));

    try {
      await axios.put(
        `http://localhost:8080/api/admin/products/${id}`,
        data,
        {
          headers: {
            Authorization: "Bearer " + token,
            "Content-Type": "multipart/form-data",
          },
        }
      );

      alert("Cập nhật thành công");
      navigate("/admin/products");

    } catch (err) {
      console.log(err);
      if (err.response?.status === 401) {
        localStorage.removeItem("adminToken");
        navigate("/admin/login");
      }
    }
  };

  return (
    <>
      <AdminHeader />

      <div style={{ height: 70 }} />

      <div style={styles.wrapper}>
        <h2 style={styles.title}>Cập nhật sản phẩm</h2>

        {/* FORM */}
        <div style={styles.form}>
          {Object.keys(form).map((key) =>
            key !== "description" && key !== "status" ? (
              <div key={key} style={styles.inputGroup}>
                <label>{key}</label>
                <input
                  name={key}
                  value={form[key] || ""}
                  onChange={handleChange}
                  style={styles.input}
                />
              </div>
            ) : null
          )}

          {/* status */}
          <div style={styles.inputGroup}>
            <label>Trạng thái</label>
            <select
              name="status"
              value={form.status || "ACTIVE"}
              onChange={handleChange}
              style={styles.input}
            >
              <option value="ACTIVE">ACTIVE</option>
              <option value="INACTIVE">INACTIVE</option>
            </select>
          </div>

          {/* mô tả */}
          <div style={{ ...styles.inputGroup, gridColumn: "1 / span 2" }}>
            <label>Mô tả</label>
            <textarea
              name="description"
              value={form.description || ""}
              onChange={handleChange}
              style={styles.textarea}
            />
          </div>
        </div>

        {/* ẢNH CŨ */}
        <h3 style={styles.sectionTitle}>Ảnh hiện tại</h3>
        <div style={styles.imageContainer}>
          {oldImages.map((img) => (
            <div key={img.id} style={styles.imageBox}>
              <img src={img.imageUrl} style={styles.image} alt="" />

              <button style={styles.deleteBtn} onClick={() => deleteOldImage(img.id)}>
                X
              </button>
            </div>
          ))}
        </div>

        {/* ẢNH MỚI */}
        <h3 style={styles.sectionTitle}>Thêm ảnh mới</h3>
        <label style={styles.addImageBox}>
          <p>Chọn ảnh</p>
          <input type="file" multiple hidden onChange={handleAddImages} />
        </label>

        <div style={styles.imageContainer}>
          {newImages.map((file, index) => (
            <div key={index} style={styles.imageBox}>
              <img src={URL.createObjectURL(file)} style={styles.image} />
              <button style={styles.deleteBtn} onClick={() => deleteNewImage(index)}>
                X
              </button>
            </div>
          ))}
        </div>

        <button style={styles.saveBtn} onClick={handleSubmit}>
          Cập nhật sản phẩm
        </button>
      </div>
    </>
  );
}


const styles = {
  wrapper: { padding: 30, maxWidth: 1200, margin: "auto" },
  title: { fontSize: 24, marginBottom: 20 },
  form: {
    display: "grid",
    gridTemplateColumns: "repeat(2, 1fr)",
    gap: 20,
    background: "#fff",
    padding: 20,
    borderRadius: 10,
    border: "1px solid #eee",
  },
  inputGroup: { display: "flex", flexDirection: "column" },
  input: { padding: 10, border: "1px solid #ccc", borderRadius: 6 },
  textarea: { width: "100%", height: 120, padding: 10, border: "1px solid #ccc", borderRadius: 6 },
  sectionTitle: { fontSize: 18, marginTop: 25, marginBottom: 10 },
  imageContainer: { display: "flex", flexWrap: "wrap", gap: 15 },
  imageBox: { width: 150, height: 150, borderRadius: 8, overflow: "hidden", position: "relative", background: "#fafafa", border: "1px solid #ddd" },
  image: { width: "100%", height: "100%", objectFit: "cover" },
  deleteBtn: {
    position: "absolute",
    top: 5,
    right: 5,
    background: "rgba(0,0,0,0.55)",
    padding: 4,
    borderRadius: "50%",
    color: "#fff",
    cursor: "pointer",
    border: "none",
  },
  addImageBox: {
    width: 160,
    height: 160,
    border: "2px dashed #aaa",
    borderRadius: 8,
    display: "flex",
    flexDirection: "column",
    justifyContent: "center",
    alignItems: "center",
    cursor: "pointer",
    color: "#333",
    marginBottom: 15,
  },
  saveBtn: {
    marginTop: 25,
    padding: "12px 25px",
    background: "#ee4d2d",
    color: "#fff",
    borderRadius: 6,
    border: "none",
    cursor: "pointer",
    fontSize: 16,
  },
};
