import React, { useEffect, useState } from "react";
import axios from "axios"; 
import { useNavigate } from "react-router-dom";
import PageHeader from "../components/page/PageHeader";
import { Calendar, User, Eye, ArrowRight, TrendingUp } from "lucide-react";
import "../css/page/NewsPage.css";

// Hàm loại bỏ thẻ HTML để hiển thị mô tả ngắn
const stripHtml = (html) => {
  const tmp = document.createElement("DIV");
  tmp.innerHTML = html;
  return tmp.textContent || tmp.innerText || "";
};

const parseDate = (value) => {
  if (!value) return null;

  // Trường hợp Spring trả array: [year, month, day, hour, minute, second]
  if (Array.isArray(value)) {
    const [y, m, d, h = 0, min = 0, s = 0] = value;
    return new Date(y, m - 1, d, h, min, s);
  }

  // Trường hợp ISO string
  return new Date(value);
};

export default function NewsPage() {
  const [newsList, setNewsList] = useState([]);
  const [featuredNews, setFeaturedNews] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    axios.get("http://localhost:8080/api/news")
      .then((res) => {
        const data = Array.isArray(res.data)
          ? res.data
          : (res.data?.data || []);

        // TẤT CẢ BÀI VIẾT: ID GIẢM DẦN
        setNewsList(
          [...data].sort((a, b) => (b.id || 0) - (a.id || 0))
        );

        // BÀI VIẾT TÂM ĐIỂM: VIEW CAO NHẤT
        setFeaturedNews(
          [...data]
            .sort((a, b) => (b.views || 0) - (a.views || 0))
            .slice(0, 3)
        );
      })
      .catch((err) => console.error("Lỗi load tin tức:", err));
  }, []);



// ... giữ nguyên phần import và logic useEffect ...

return (
  <div className="news-container">
    <PageHeader />

    <main className="news-content-wrapper">
      {/* ===== HERO SECTION ===== */}
      <section className="news-hero-modern">
        <div className="hero-overlay"></div>
        <div className="container">
          <div className="hero-text">
            <span className="badge-tech"><TrendingUp size={14}/> Xu hướng 2025</span>
            <h1>Khám Phá Thế Giới <br/><span>Công Nghệ</span></h1>
            <p>Cập nhật những tin tức sốt dẻo nhất về smartphone, phụ kiện và các ưu đãi không thể bỏ lỡ tại Mobile Store.</p>
          </div>
        </div>
      </section>

      {/* ===== FEATURED SECTION ===== */}
      <section className="featured-section">
        <div className="container">
          <div className="section-header">
            <h2 className="premium-title">Bài viết tâm điểm</h2>
            <div className="title-underline"></div>
          </div>
          
          <div className="featured-grid-modern">
            {featuredNews.map((news, index) => (
              <div 
                key={news.id} 
                className={`featured-item item-${index}`}
                onClick={() => navigate(`/news/${news.id}`)}
                style={{ 
                  backgroundImage: `linear-gradient(to bottom, rgba(0,0,0,0) 30%, rgba(0,0,0,0.85)), url(${news.thumbnail || 'https://via.placeholder.com/800x600'})`,
                  backgroundSize: 'cover',
                  backgroundPosition: 'center'
                }}
              >
                {/* Badge View cao cấp */}
                <div className="view-badge">
                  <Eye size={14} /> {news.views?.toLocaleString() || 0}
                </div>

                <div className="featured-card-content">
                  <div className="card-meta">
                    <span className="date-tag"><Calendar size={13}/> {parseDate(news.createdAt)?.toLocaleDateString("vi-VN")}</span>
                  </div>
                  <h3 className="featured-title-text">{news.title}</h3>
                  {index === 0 && <p className="featured-desc">{stripHtml(news.content).slice(0, 120)}...</p>}
                  <button className="read-more-btn-v2">Khám phá ngay <ArrowRight size={16}/></button>
                </div>
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* ===== ALL NEWS GRID ===== */}
      <section className="all-news-section">
        <div className="container">
          <div className="section-header">
            <h2 className="premium-title">Bản tin công nghệ</h2>
          </div>

          <div className="modern-news-grid">
            {newsList.map((news) => (
              <article key={news.id} className="modern-news-card" onClick={() => navigate(`/news/${news.id}`)}>
                <div className="card-image-placeholder">
                  {news.thumbnail ? (
                    <img src={news.thumbnail} alt={news.title} style={{ width: '100%', height: '100%', objectFit: 'cover' }} />
                  ) : (
                    <div className="no-image-gradient">No Image</div>
                  )}
                  <div className="category-overlay">Tin công nghệ</div>
                </div>

                <div className="card-body">
                  <div className="card-info">
                    <span className="author-name"><User size={12}/> {news.author || 'King Mobile'}</span>
                    <span className="date-text">{parseDate(news.createdAt)?.toLocaleDateString("vi-VN")}</span>
                  </div>
                  <h3 className="card-title">{news.title}</h3>
                  <p className="card-excerpt">{stripHtml(news.content).slice(0, 90)}...</p>
                  
                  <div className="card-footer">
                    <span className="view-count-text"><Eye size={13}/> {news.views || 0} lượt xem</span>
                    <span className="read-more-link">Đọc tiếp <ArrowRight size={14}/></span>
                  </div>
                </div>
              </article>
            ))}
          </div>
        </div>
      </section>
    </main>
  </div>
);
}
