import React, { useEffect, useState } from "react";

import { Link, useNavigate } from "react-router-dom";
import { Plus } from "lucide-react";

// ⬆️ IMPORT HEADER ADMIN
import AdminHeader from "../page/AdminHeader";

export default function ProductManagement() {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  // ================= LOAD SẢN PHẨM =================
  useEffect(() => {
    axios
      .get("/products")
      .then((res) => {
        const data = Array.isArray(res.data) ? res.data : res.data.data;
        setProducts(data || []);
      })
      .catch((err) => console.error("Lỗi load sản phẩm:", err))
      .finally(() => setLoading(false));
  }, []);

  const formatPrice = (price) =>
    price?.toLocaleString("vi-VN") + "₫";

  return (
    <>
      {/* ============= HEADER ADMIN ============= */}
      <AdminHeader />

      {/* tránh bị che do header fixed */}
      <div style={{ height: 70 }}></div>

      <div style={styles.wrapper}>

        {/* ================= HEADER QUẢN LÝ SP ================= */}
        <div style={styles.headerTop}>
          <h2 style={styles.pageTitle}>Quản lý sản phẩm</h2>

          <Link to="/admin/products/add" style={styles.addBtn}>
            <Plus size={18} />
            Thêm sản phẩm
          </Link>
        </div>

        {/* ================= BẢNG SẢN PHẨM ================= */}
        <div style={styles.tableHeader}>
          <div style={{ width: "40%" }}>Sản phẩm</div>
          <div style={{ width: "10%" }}>Doanh số</div>
          <div style={{ width: "10%" }}>Giá</div>
          <div style={{ width: "10%" }}>Kho</div>
          <div style={{ width: "10%" }}>Kho</div>
          <div style={{ width: "15%" }}>Thao tác</div>
        </div>

        {loading ? (
          <p style={{ padding: 20 }}>Đang tải...</p>
        ) : products.length === 0 ? (
          <p style={{ padding: 20 }}>Chưa có sản phẩm nào.</p>
        ) : (
          products.map((p) => (
            <div key={p.id} style={styles.tableRow}>
              {/* TÊN + IMAGE */}
              <div style={{ ...styles.rowCell, width: "40%" }}>
                <div style={styles.productCell}>
                  <img
                    src={
                      p.images?.[0]?.imageUrl?.replace(/\\/g, "/") ||
                      "/no-image.png"
                    }
                    style={styles.thumbnail}
                    alt={p.name}
                  />
                  <div>
                    <strong>{p.name}</strong>
                    <div style={styles.skuText}>ID: {p.id}</div>
                  </div>
                </div>
              </div>

              {/* DOANH SỐ */}
              <div style={{ ...styles.rowCell, width: "10%" }}>0</div>

              {/* GIÁ */}
              <div style={{ ...styles.rowCell, width: "10%" }}>
                {formatPrice(p.finalPrice ?? p.price)}
              </div>

              {/* KHO */}
              <div style={{ ...styles.rowCell, width: "10%" }}>
                {p.quantityInStock}
              </div>
               <div style={{ ...styles.rowCell, width: "10%" }}>
                {p.status}
              </div>

              {/* ACTION */}
              <div style={{ ...styles.rowCell, width: "15%" }}>
                <Link
                  to={`/admin/products/edit/${p.id}`}
                  style={styles.actionLink}
                >
                  Cập nhật
                </Link>

                <Link to={`/product/${p.id}`} style={styles.actionLink}>
                  Xem chi tiết
                </Link>
              </div>
            </div>
          ))
        )}
      </div>
    </>
  );
}

/* ============================== CSS STYLES ============================== */

const styles = {
  wrapper: {
    padding: "25px 20px",
    maxWidth: 1350,
    margin: "auto",
  },

  // PAGE TITLE
  pageTitle: {
    fontSize: 26,
    fontWeight: 600,
    color: "#333",
  },

  headerTop: {
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
    marginBottom: 25,
  },

  addBtn: {
    display: "flex",
    alignItems: "center",
    gap: 6,
    background: "#ee4d2d",
    color: "#fff",
    textDecoration: "none",
    padding: "10px 16px",
    borderRadius: 6,
    fontSize: 14,
  },

  // TABLE HEADER
  tableHeader: {
    display: "flex",
    padding: "12px 10px",
    background: "#fafafa",
    border: "1px solid #eee",
    borderRadius: 6,
    fontWeight: 600,
    color: "#555",
    marginBottom: 10,
  },

  // TABLE ROW
  tableRow: {
    display: "flex",
    padding: "12px 10px",
    borderBottom: "1px solid #eee",
    alignItems: "center",
    transition: "0.2s",
  },

  rowCell: {
    fontSize: 14,
    color: "#333",
  },

  productCell: {
    display: "flex",
    alignItems: "center",
    gap: 12,
  },

  thumbnail: {
    width: 65,
    height: 65,
    objectFit: "cover",
    borderRadius: 6,
    border: "1px solid #eee",
  },

  skuText: {
    fontSize: 12,
    color: "#888",
  },

  actionLink: {
    display: "block",
    marginBottom: 4,
    color: "#007bff",
    textDecoration: "none",
    cursor: "pointer",
  },
};
