import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import axios from "../api/axios";
import PageHeader from "../components/page/PageHeader";
import "../css/page/orderDetail.css";

export default function OrderDetailPage() {
  const { id } = useParams();
  const navigate = useNavigate();

  const [order, setOrder] = useState(null);
  const [loading, setLoading] = useState(true);

  /* ================= LOAD ORDER ================= */
  useEffect(() => {
    axios
      .get(`/orders/${id}`)
      .then((res) => {
        const data = res.data?.data ?? res.data;
        setOrder(data);
      })
      .catch(() => {
        alert("Không tìm thấy đơn hàng");
        navigate("/my-orders");
      })
      .finally(() => setLoading(false));
  }, [id, navigate]);

  const formatMoney = (v) =>
    v?.toLocaleString("vi-VN") + " ₫";

  const formatDate = (d) =>
    d ? new Date(d).toLocaleString("vi-VN") : "-";

  if (loading) return <p className="od-loading">Đang tải...</p>;
  if (!order) return null;

  return (
    <>
      <PageHeader />

      <div className="od-wrapper">
        <div className="od-card">

          {/* ===== HEADER ===== */}
          <div className="od-header">
            <div>
              <h2>Đơn hàng #{order.id}</h2>
              <p>Ngày đặt: {formatDate(order.createdAt)}</p>
            </div>

            <span className={`od-status status-${order.status}`}>
              {order.status}
            </span>
          </div>

          {/* ===== SHIPPING INFO ===== */}
          <div className="od-section">
            <h3>Thông tin giao hàng</h3>
            <div className="od-info">
              <p><b>Họ tên:</b> {order.fullName}</p>
              <p><b>SĐT:</b> {order.phone}</p>
              <p><b>Địa chỉ:</b> {order.address}</p>
              {order.note && <p><b>Ghi chú:</b> {order.note}</p>}
            </div>
          </div>

          {/* ===== ITEMS ===== */}
          <div className="od-section">
            <h3>Sản phẩm</h3>

            <div className="od-items">
              {order.items.map((i) => (
                <div key={i.id} className="od-item">
                  <div className="od-item-left">
                    <img src={i.image} alt={i.productName} />
                    <div>
                      <p className="od-item-name">{i.productName}</p>
                      <p className="od-item-qty">x{i.quantity}</p>
                    </div>
                  </div>

                  <div className="od-item-price">
                    {formatMoney(i.price * i.quantity)}
                  </div>
                </div>
              ))}
            </div>
          </div>

          {/* ===== TOTAL ===== */}
          <div className="od-total">
            <span>Tổng tiền</span>
            <span className="price">{formatMoney(order.totalPrice)}</span>
          </div>

          {/* ===== ACTION ===== */}
          <div className="od-actions">
            <button onClick={() => navigate("/my-orders")}>
              ← Quay lại đơn hàng
            </button>
          </div>

        </div>
      </div>
    </>
  );
}
