import React, { useState } from "react";
import "./PageHeader.css";
import { useNavigate } from "react-router-dom";

export default function PageHeader({ user, onLogout }) {
  const navigate = useNavigate();
  const [openMenu, setOpenMenu] = useState(false);

  const toggleMenu = () => setOpenMenu(!openMenu);

  const getCartCount = () => {
    if (!user) return 0;
    const key = `cart_${user.email}`;
    const cart = JSON.parse(localStorage.getItem(key)) || [];
    return cart.reduce((sum, i) => sum + i.quantity, 0);
  };

  const goToCart = () => {
    if (!user) {
      alert("B·∫°n c·∫ßn ƒëƒÉng nh·∫≠p ƒë·ªÉ xem gi·ªè h√†ng!");
      navigate("/login");
      return;
    }
    navigate("/cart");
  };

  return (
    <>
      <nav className="cp-header">

        <div className="cp-logo" onClick={() => navigate("/")}>
          Cellphone<span>S</span>
        </div>

        {/* search */}
        <input className="cp-search" placeholder="B·∫°n mu·ªën t√¨m g√¨?" />

        <div className="cp-icons">

          {user ? (
            <div className="avatar">
              <img src={user.avatar} onClick={toggleMenu} />
              {openMenu && (
                <div className="user-menu">
                  <div onClick={() => navigate("/profile")}>üë§ T√†i kho·∫£n</div>
                  <div onClick={() => navigate("/my-orders")}>üì¶ ƒê∆°n h√†ng</div>
                  <div onClick={onLogout}>üö™ ƒêƒÉng xu·∫•t</div>
                </div>
              )}
            </div>
          ) : (
            <button onClick={() => navigate("/login")}>ƒêƒÉng nh·∫≠p</button>
          )}

          <div className="cp-cart" onClick={goToCart}>
            üõí
            {getCartCount() > 0 && (
              <span className="cp-cart-count">{getCartCount()}</span>
            )}
          </div>

        </div>
      </nav>
    </>
  );
}
