import React from "react";
import { Navbar, Nav, NavDropdown, Container } from "react-bootstrap";
import { useNavigate } from "react-router-dom";
import "./PageHeader.css";

export default function PageHeader({ user, onLogout, onToggleProfile }) {
  const navigate = useNavigate();

  return (
    <Navbar expand="lg" bg="light" className="shadow-sm mb-4 rounded-3">
      <Container>
        <Navbar.Brand onClick={() => navigate("/")} style={{ color: "#ff7a29", fontWeight: "bold" }}>
          üå∏ MyApp
        </Navbar.Brand>
        <Navbar.Toggle aria-controls="basic-navbar-nav" />
        <Navbar.Collapse id="basic-navbar-nav">
          <Nav className="me-auto">
            <Nav.Link onClick={() => navigate("/")}>Trang ch·ªß</Nav.Link>
            <NavDropdown title="Danh m·ª•c" id="category-dropdown">
              <NavDropdown.Item onClick={() => navigate("/category/phones")}>ƒêi·ªán tho·∫°i</NavDropdown.Item>
              <NavDropdown.Item onClick={() => navigate("/category/laptops")}>Laptop</NavDropdown.Item>
              {/* ... th√™m c√°c m·ª•c nh∆∞ CellphoneS */}
            </NavDropdown>
            <Nav.Link onClick={() => navigate("/promotions")}>Khuy·∫øn m√£i</Nav.Link>
            <Nav.Link onClick={() => navigate("/news")}>Tin c√¥ng ngh·ªá</Nav.Link>
          </Nav>

          <Nav className="ms-auto align-items-center gap-3">
            {user ? (
              <>
                <span style={{ cursor: "pointer" }} onClick={onToggleProfile}>
                  {user.fullName}
                </span>
                <img
                  src={user.avatar}
                  alt="avatar"
                  onClick={onToggleProfile}
                  className="rounded-circle border border-2"
                  style={{ width: "40px", height: "40px", objectFit: "cover", cursor: "pointer" }}
                />
                <button className="btn btn-outline-danger btn-sm rounded-pill" onClick={onLogout}>
                  Logout
                </button>
              </>
            ) : (
              <button className="btn btn-primary btn-sm" onClick={() => navigate("/login")}>
                ƒêƒÉng nh·∫≠p / ƒêƒÉng k√Ω
              </button>
            )}
          </Nav>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
}
