import React, { useState } from "react";
import axios from "axios";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import { useLocation, useNavigate } from "react-router-dom";

function ResetPasswordPage() {
  const location = useLocation();
  const navigate = useNavigate();
  const [data, setData] = useState({
    email: location.state?.email || "",
    otpCode: "",
  });
  const [loading, setLoading] = useState(false);

  const handleChangeInput = (e) => {
    const { name, value } = e.target;
    setData({ ...data, [name]: value });
  };

  async function handleSubmit(e) {
    e.preventDefault();
    try {
      setLoading(true);
      console.log("data: ",data);
      
      const res = await axios.post(
        "http://localhost:8080/api/auth/verify-reset-otp",
        data
      );
      if (res.status === 200) {
        toast.success("ƒê·∫∑t l·∫°i m·∫≠t kh·∫©u th√†nh c√¥ng üéâ", {
          position: "top-right",
        });
        setData({ email: "", otpCode: "" });
        setTimeout(()=>{
          navigate("/login")
        },1000)
      }
    } catch (error) {
      console.error("Reset password error:", error);
      toast.error("OTP kh√¥ng h·ª£p l·ªá ho·∫∑c ƒë√£ h·∫øt h·∫°n ‚ùå", {
        position: "top-right",
      });
    } finally {
      setLoading(false);
    }
  }

  return (
    <div
      className="bg-light min-vh-100 d-flex justify-content-center align-items-center"
      style={{ background: "#fffaf5" }}
    >
      <div className="col-md-5">
        <div
          className="card border-0 shadow-lg rounded-4"
          style={{ background: "#ffffff" }}
        >
          <div className="card-body p-5">
            <h2
              className="text-center mb-4 fw-bold"
              style={{ color: "#ff7a29" }}
            >
              Reset Password
            </h2>
            <p className="text-center text-muted mb-5">
              Enter your email, OTP code, and new password
            </p>

            <form onSubmit={handleSubmit}>
              {/* Email */}
              <div className="mb-3">
                <label htmlFor="email" className="form-label fw-semibold">
                  Email address
                </label>
                <input
                  type="email"
                  className="form-control rounded-pill px-3"
                  id="email"
                  name="email"
                  value={data.email}
                  onChange={handleChangeInput}
                  placeholder="Enter your email"
                  disabled={loading}
                />
              </div>

              {/* OTP */}
              <div className="mb-3">
                <label htmlFor="otpCode" className="form-label fw-semibold">
                  otpCode Code
                </label>
                <input
                  type="text"
                  className="form-control rounded-pill px-3"
                  id="otpCode"
                  name="otpCode"
                  value={data.otpCode}
                  onChange={handleChangeInput}
                  placeholder="Enter the otp sent to your email"
                  disabled={loading}
                />
              </div>

              {/* Submit Button */}
              <div className="d-grid">
                <button
                  type="submit"
                  className="btn btn-lg text-white rounded-pill shadow d-flex align-items-center justify-content-center"
                  style={{
                    background: "linear-gradient(135deg, #ff7a29, #ff9e57)",
                    border: "none",
                    opacity: loading ? 0.8 : 1,
                  }}
                  disabled={loading}
                >
                  {loading ? (
                    <>
                      <span
                        className="spinner-border spinner-border-sm me-2"
                        role="status"
                        aria-hidden="true"
                      ></span>
                      Resetting...
                    </>
                  ) : (
                    "Reset Password"
                  )}
                </button>
              </div>
            </form>
          </div>
        </div>
      </div>

      {/* Toast */}
      <ToastContainer />
    </div>
  );
}

export default ResetPasswordPage;
