import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import adminAxios from "../../api/adminAxios";
import AdminHeader from "../page/AdminHeader";
import "./add.css";

function AddNews() {
  const navigate = useNavigate();
  const [topic, setTopic] = useState("");
  const [selectedImage, setSelectedImage] = useState(null); // Lưu file ảnh
  const [preview, setPreview] = useState(null); // Hiển thị xem trước
  const [news, setNews] = useState({
    title: "",
    content: "",
    author: "King Mobile",
  });
  const [loadingAI, setLoadingAI] = useState(false);

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setSelectedImage(file);
      setPreview(URL.createObjectURL(file));
    }
  };

  const handleAdd = async () => {
    if (!news.title.trim() || !news.content.trim()) {
      alert("Vui lòng nhập đủ Tiêu đề và Nội dung!");
      return;
    }

    try {
      // SỬ DỤNG FORMDATA ĐỂ GỬI FILE
      const formData = new FormData();
      formData.append("title", news.title);
      formData.append("content", news.content);
      formData.append("author", news.author);
      if (selectedImage) {
        formData.append("image", selectedImage);
      }

      await adminAxios.post("/news", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });

      alert("Thêm tin tức thành công!");
      navigate("/admin/news");
    } catch (err) {
      alert("Thêm tin tức thất bại!");
    }
  };

  /* ================= AI GENERATE ================= */
  const handleGenerateAI = async () => {
    if (!topic.trim()) {
      alert("Vui lòng nhập chủ đề trước khi tạo AI!");
      return;
    }

    try {
      setLoadingAI(true);

      const res = await adminAxios.post("/news/ai-generate", {
        topic: topic.trim(),
      });

      setNews((prev) => ({
        ...prev,
        title: res.data?.title || "",
        content: res.data?.content || "",
      }));
    } catch (err) {
      console.error("AI error:", err.response?.data || err.message);
      alert("AI tạo bài viết thất bại!");
    } finally {
      setLoadingAI(false);
    }
  };

  
  return (
    <>
      <AdminHeader />
      <div style={{ height: 70 }} />
      <div className="news-add-container">
        <h2 className="news-add-header">Thêm Tin tức</h2>
        <div className="news-add-form">
          {/* INPUT CHỌN ẢNH */}
          <div className="form-group">
            <label>Ảnh đại diện</label>
            <input type="file" accept="image/*" onChange={handleImageChange} />
            {preview && <img src={preview} alt="Preview" style={{ width: 150, marginTop: 10 }} />}
          </div>
          
          <div className="form-group">
            <label>Chủ đề / Ý tưởng</label>
            <input type="text" value={topic} onChange={(e) => setTopic(e.target.value)} placeholder="Ví dụ: Giảm giá ngày sinh nhật" />
          </div>

          <div className="form-group">
            <label>Tiêu đề</label>
            <input type="text" value={news.title} onChange={(e) => setNews({ ...news, title: e.target.value })} />
          </div>

          <div className="form-group">
            <label>Nội dung</label>
            <textarea rows={10} value={news.content} onChange={(e) => setNews({ ...news, content: e.target.value })} />
          </div>

          <div className="form-actions">
            <button className="btn-primary" onClick={handleGenerateAI} disabled={loadingAI}>
              {loadingAI ? "Đang tạo..." : "Tạo bằng AI"}
            </button>
            <button className="btn-success" onClick={handleAdd}>Thêm Tin tức</button>
            <button className="btn-cancel" onClick={() => navigate("/admin/news")}>Hủy</button>
          </div>
        </div>
      </div>
    </>
  );
}
export default AddNews;
